import React, { useState } from 'react'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faBars} from '@fortawesome/free-solid-svg-icons'
import { Link } from "react-router-dom";

function Navigation(){

    const [showMenu , setShowMenu]= useState(false)


     let menu 
     let menuMask

     if(showMenu){
        menu=<div className="fixed bg-black top-0 left-0 w-4/5 h-full z-50 shadow">
            
            Here's Your Menu !</div>

            menuMask=
                        <div
                        className="bg-black fixed top-0 left-0 w-full h-full z-50 p-3"
                        onClick={() => setShowMenu(false)}
                        >
             <ul>

                    <li>
                    <Link to="/" className="text-blue-500">Home</Link>
                    </li>


                        <li>
                        <Link to="/" className="text-blue-500">About</Link>
                        </li>

                        </ul>

                        </div>

    

        return(
                <nav>
                    <span className="text-xt">
                         <FontAwesomeIcon
                         icon={faBars} 
                         onClick = {() => setShowMenu(!showMenu)}

                         
                         
                         />


                    </span>

                    {menuMask}
                    {menu}

                    </nav>

                    

        )
        }
}

export default Navigation
